<template>
  <div class="container" v-if="topic.avatar">
    <div class="wrapper">
      <div class="header">
        <div class="avatar">
          <img :src="topic.avatar" alt="">
        </div>
        <span class="name">{{topic.nickname}}</span>
      </div>
      <div class="text">
        <span>{{topic.title}}</span>
      </div>
      <div class="img">
        <img :src="topic.picUrl" alt="">
      </div>
      <div class="look">
        <i class="icon"></i>
        <span>{{convert}}人看过</span>
      </div>
    </div>
  </div>
</template>

<script type="text/ecmascript-6">
  export default {
    //带头像的上下结构 0和10都适用
    props:["topic"],
    computed: {
      convert:function() {
        let num = this.topic.readCount
        if(num>=10000){
          num = Math.floor(Math.round(num/1000)/10)+"w"
        }else if(num>=1000){
          num = Math.floor(Math.round(num/1000)/10)+"k"
        }
        return num;
      }
    },
  }
</script>

